<?phpclass Base_Model extends CI_Model {    public $tablename = '';    protected $where = array();    function __construct() {        parent::__construct();        $this->load->database();        $this->tablename = get_class($this); //获取当前类名        $this->tablename = $this->setTableName(mb_strtolower(mb_substr($this->tablename, 0, -6))); //截取字符    }    public function setTableName($tablename) {        $this->tablename = $tablename;        return $this->tablename;    }    public function getTableName() {        return $this->tablename;    }        public function getOne($data = array(), $table = '') {            if (!$table) {                    $table = $this->tablename;            }            $data['num'] = 1;            $rs = $this->index($data);            return ((count($rs) > 0) ? array_shift($rs) : array());    }    public function del($data) {            $data = !is_array($data) ? array('id' => $data) : $data;            return $this->db->delete($this->tablename, $data);    }    /*     * 给sql语句使用，拆分多个参数     */    public function exp($data) {    	if (!is_array($data)) {        	$data = explode('-', $data);    	}    	$data = array_filter($data);        $data = implode(",", $data);        return $data;    }        public function split($data) {    	if (!is_array($data)) {        	$data = explode('-', $data);    	}    	$data = array_filter($data);        return $data;    }}